import java.util.Arrays;

public class PancakeSort {
    public static void main(String[] args) {
        int[] arr = {1234567890, 1234567891, 1234567892};
        System.out.println("Original Array: " + Arrays.toString(arr));
        pancakeSort(arr);
        System.out.println("Sorted Array: " + Arrays.toString(arr));
    }

    // This function sorts the given array using the pancake sort algorithm.
    private static void pancakeSort(int[] arr) {
        int n = arr.length;

        // Iterate through each element in the array from last to first
        for (int curr_size = n; curr_size > 1; --curr_size) {
            // Find index of the maximum element in unsorted array
            int maxIndex = findMaxIndex(arr, curr_size);

            // Move maximum number to front if it's not already at front
            if (maxIndex != 0) {
                flip(arr, maxIndex); // Flip to bring max to front
            }

            // Now move it from front to its correct position by flipping again 
            flip(arr, curr_size - 1);
        }
    }

    // Function that flips an array up to a given index
    private static void flip(int[] arr, int idx) {
        int start = 0;

        while (start < idx) {
            // Swap elements at start and idx positions
            int temp = arr[start];
            arr[start] = arr[idx];
            arr[idx] = temp;

             start++;
             idx--;
         }
     }

     // Function that returns the index of the maximum element in an array up to a given size 
     private static int findMaxIndex(int[] arr, int n) {
         int maxIdx = 0;
         for (int i=1; i<n; i++) { 
             if (arr[i] > arr[maxIdx]) { 
                 maxIdx=i; 
             }  
         }  
         return maxIdx;  
     }
}
