import { Component } from '@angular/core';
import * as pdfjsLib from 'pdfjs-dist/build/pdf';

@Component({
  selector: 'app',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'PDF to Image Converter';

  async convertPdfToImages(pdfUrl: string) {
    // Load the PDF document
    const loadingTask = pdfjsLib.getDocument(pdfUrl);

    try {
      const pdf = await loadingTask.promise;
      const numPages = pdf.numPages;

      for (let pageNum = 1; pageNum <= numPages; pageNum++) {
        // Get each page
        const page = await pdf.getPage(pageNum);

        // Set scale for rendering the image
        const scale = 2;
        const viewport = page.getViewport({ scale });

        // Prepare canvas using PDF.page dimensions
        const canvasFactory = new pdfjsLib.CanvasFactory();
        const canvasAndContext =
          canvasFactory.create(viewport.width, viewport.height);

        // Render PDF page into context of the canvas
        await page.render({
         canvasContext: canvasAndContext.context,
          viewport,
          continueCallback() {}
         }).promise;

         // Convert Canvas to Image and handle it accordingly 
         this.handleImage(canvasAndContext.canvas.toDataURL('image/png'), `page-${pageNum}.png`);

         // Clean up after rendering each image if necessary.
         canvasFactory.destroy(canvasAndContext);
       }
     } catch (error) {
       console.error('Error during PDF conversion:', error);
     }
   }

   handleImage(dataUrl: string, filename: string) {
     // Create a link element to download the image file 
     let linkElement = document.createElement('a');
     linkElement.href = dataUrl;
     linkElement.download= filename;

     document.body.appendChild(linkElement); 

     linkElement.click(); 

     document.body.removeChild(linkElement); 
   }
}
