```matlab
function [coeff_path, mse_values] = lasso_l1reg(X, y)
    % LASSO_L1REG performs linear regression with L1 regularization (lasso)
    % and evaluates the mean squared error (MSE) of the resulting model.
    %
    % Inputs:
    %   X - n x m matrix of features
    %   y - n x 1 vector of target values
    %
    % Outputs:
    %   coeff_path - Coefficient paths for different values of lambda
    %   mse_values  - Mean squared error for each value of lambda


	% Set up parameters for lasso regression
	lambda_vals = logspace(-4, 0, 100);  % Regularization parameter range 
	mse_values = zeros(length(lambda_vals), 1);  
	[n, m] = size(X);

	% Initialize coefficient path storage 
	coeff_path = zeros(m, length(lambda_vals));  

	for i = 1:length(lambda_vals)
        lambda = lambda_vals(i);

        [B, FitInfo] = lasso(X,y,'Lambda',lambda,'Alpha',1.0);  

        coeff_path(:, i) = B; 

        y_predicted = X * B;  

        mse_values(i) = mean((y - y_predicted).^2);  
	end

	% Plotting coefficient paths 
	figure;
	for j=1:m
	    plot(lambda_vals, coeff_path(j,:), 'DisplayName', sprintf('Feature%d', j));
	    hold on;
	end

	set(gca,'XScale','log');
	title('LASSO Coefficient Paths');
xlabel('Regularization Parameter (\lambda)');
ylabel('Coefficients');
legend show;
grid on;

end
