        // Simulate submission process
        echo "Submitting draft for review...\n";
        // Review logic can be added here
        $this->isReviewed = true; 
        echo "Draft reviewed.\n";
    }

    public function signOrder() {
        if ($this->isReviewed) {
            // Simulate signing process
            echo "Signing final version...\n";
            $this->isSigned = true; 
            echo "Executive Order signed.\n";
            return true;
        } else {
            echo "Cannot sign until reviewed.\n";
            return false;
        }

    }

}

