```csharp
public class PlayerController : MonoBehaviour {
    public float speed = 10f;

    void Update () {
        // Get the camera's rotation
        Quaternion headRotation = Camera.main.transform.rotation;

        // Rotate the player object to match the camera's yaw (horizontal rotation)
        Vector3 newDirection = new Vector3(headRotation.eulerAngles.x, headRotation.eulerAngles.y, 0);

        // Apply this direction to rotate your player object
        transform.rotation = Quaternion.Euler(newDirection);

        // Optional: Move forward in relation to player's facing direction
        if (Input.GetKey(KeyCode.W)) { 
            transform.position += transform.forward * speed * Time.deltaTime;
        }
    }
}
