interface Person extends Document {
  firstName: string;
  lastName: string;
}

const personSchema = new mongoose.Schema({
  firstName: { type: String, required: true },
  lastName: { type: String, required: true }
});

const PersonModel = mongoose.model<Person>('Person', personSchema);

export const searchPeople = async (input: string): Promise<Person[]> => {
  if (typeof input !== 'string' || input.trim() === '') {
    throw new Error('Input must be a non-empty string');
  }

  // Split the input into parts to handle full names
  const nameParts = input.trim().split(' ');

  // Build the query based on whether it's a full name or just one part
  let query;

  if (nameParts.length === 2) {
    // Full name case
    const [firstName, lastName] = nameParts;
    query = { 
      $or: [
        { firstName: new RegExp(firstName, 'i') }, 
        { lastName: new RegExp(lastName, 'i') }
      ]
    };

   } else if (nameParts.length ===1) {
     // First or Last Name case
     const singlePart= nameParts[0];
     query= {$or:[
       {firstNmae:new RegExp(singlePart,'i')},
       {lastNmae:new RegExp(singlePart,'i')}
     ]}
   }

   return await PersonModel.find(query);
};
