import Foundation

// Function to perform counting sort on the array based on a specific digit represented by exp
func countingSort(_ arr: inout [Int], exp: Int) {
    let n = arr.count
    var output = Array(repeating: 0, count: n) // Output array that will hold the sorted numbers
    var count = Array(repeating: 0, count: 10) // Count array for digits (0-9)

    // Store the count of occurrences of each digit in 'count'
    for i in 0..<n {
        let index = (arr[i] / exp) % 10
        count[index] += 1
    }

    // Change count[i] so that it contains actual position of this digit in output[]
    for i in 1..<10 {
        count[i] += count[i - 1]
    }

    // Build the output array using the counts from 'count'
    for i in (0..<n).reversed() { 
        let index = (arr[i] / exp) % 10 
        output[count[index] - 1] = arr[i]
        count[index] -= 1 
   }

   // Copying the sorted elements back into original array
   for i in 0..<n {
       arr[i] = output[i]
   }
}

// Main function to implement radix sort algorithm on an integer array.
func radixSort(_ arr: inout [Int]) {

     guard !arr.isEmpty else { return } 

     // Find maximum number to know number of digits.
     let maxVal = arr.max() ?? Int.min

     // Apply counting sort to sort elements based on place value.
     var exp = 1 

     while maxVal / exp > 0 { 
         countingSort(&arr, exp: exp)
         exp *= 10  
      }
}

// Example usage:
var numbersToSort = [170,45,75,90,802,24,2,66]
print("Original Array:", numbersToSort)
radixSort(&numbersToSort)
print("Sorted Array:", numbersToSort)
