import Foundation

// Function to fetch synonyms from the Thesaurus API
func getSynonyms(for word: String, completion: @escaping ([String]?) -> Void) {
    let urlString = "https://api.thesaurus.com/synonyms/\(word)"
    guard let url = URL(string: urlString) else {
        completion(nil)
        return
    }

    let task = URLSession.shared.dataTask(with: url) { data, response, error in
        if let error = error {
            print("Error fetching synonyms: \(error)")
            completion(nil)
            return
        }

        guard let data = data else {
            completion(nil)
            return
        }

        do {
            // Assuming the API returns a JSON array of synonyms for the word.
            if let jsonArray = try JSONSerialization.jsonObject(with: data, options: []) as? [String] {
                completion(jsonArray)
                return
            }
        } catch {
            print("Error parsing JSON response")
        }

        // If no valid response is received.
        completion(nil)
    }

    task.resume()
}

// Function to compress text by substituting words with their respective synonyms 
func compressText(_ text: String, completion: @escaping (String?) -> Void) {
    var words = text.split(separator: " ").map(String.init)

    // Create a dispatch group to handle asynchronous calls for each word's synonym retrieval.
    let dispatchGroup = DispatchGroup()

    for (index, word) in words.enumerated() {
         dispatchGroup.enter()  // Entering the group before starting an async call

         getSynonyms(for: word) { synonyms in

             if let firstSynonym = synonyms?.first {  // Use first synonym found as substitution 
                 words[index] = firstSynonym 
             } 

             dispatchGroup.leave()  // Leaving the group after async call completes  
         }
     }

     dispatchGroup.notify(queue:.main ) {  
          // Once all requests are completed:
          completions(words.joined(separator:" "))   // Join modified words back into a single string and pass it back through closure.   
      }  
}

