import java.util.Random;

class AmusementParkSimulation {
    private static final String[] RIDES = {"Roller Coaster", "Ferris Wheel", "Haunted House", "Water Slide"};
    private static final String[] RESTAURANTS = {"Pizza Place", "Burger Joint", "Ice Cream Stand"};

    public static void main(String[] args) {
        simulateDayTrip(10, 16); // Simulate from 10 AM to 4 PM
    }

    public static void simulateDayTrip(int startHour, int endHour) {
        Random random = new Random();
        List<String> schedule = new ArrayList<>();

        System.out.println("Starting the day at: " + startHour + ":00");

        for (int hour = startHour; hour < endHour; hour++) {
            if (random.nextBoolean()) { // Randomly decide if a ride is available
                String ride = RIDES[random.nextInt(RIDES.length)];
                schedule.add("Enjoying the ride: " + ride);
            } else {
                schedule.add("Ride closed. Waiting in line.");
            }

            if (random.nextBoolean()) { // Decide whether to eat or not
                String restaurant = RESTAURANTS[random.nextInt(RESTAURANTS.length)];
                schedule.add("Dining at: " + restaurant);
            }

            // Check for unexpected events like weather changes or closures
            if (random.nextDouble() < 0.1) { // 10% chance of an unexpected event occurring
                System.out.println("Unexpected event occurred! Ride closure due to maintenance.");
                break; // End simulation early due to an unexpected event.
            }

            System.out.println(hour + ":00 - Activities planned:");

           for(String activity : schedule){
               System.out.println(activity);
           }

           try{
               Thread.sleep(1000); 
           } catch(InterruptedException e){
               Thread.currentThread().interrupt();
           }

          schedule.clear(); 
       }

       System.out.println("\nEnd of the day trip!");
   }
}
