func rearrangeSentence(sentence string) string {
    words := strings.Fields(sentence)

    // Reverse the order of words
    for i, j := 0, len(words)-1; i < j; i, j = i+1, j-1 {
        words[i], words[j] = words[j], words[i]
    }

    return strings.Join(words, " ")
}

func main() {
