   use GuzzleHttp\Client;
   use GuzzleHttp\Exception\RequestException;

   function analyzeTone($text) {
       $apiKey = 'YOUR_API_KEY';
       $url = 'YOUR_SERVICE_URL/tone'; // Replace with your service URL

       $client = new Client();

       try {
           $response = $client->post($url, [
               'auth' => ['apikey', $apiKey],
               'json' => ['text' => $text]
           ]);

           // Decode JSON response
           $toneData = json_decode($response->getBody(), true);

           // Convert to XML format
