           return '<error>' . htmlspecialchars($e->getMessage()) . '</error>';
       }
   }

   function convertToXML($data) {
       // Create a new XML document
       $xml = new SimpleXMLElement('<toneAnalysis/>');

       foreach ($data['document_tone']['tones'] as $tone) {
           // Add each tone as an element with attributes
           if (isset($tone['score'])) {
               $toneElement = &$xml->addChild('tone');
               foreach ($tone as key => value) { 
