package main

import (
	"fmt"
	"time"
)

// Meeting struct represents a scheduled meeting with a date and time.
type Meeting struct {
	DateTime time.Time
}

// getNextMeeting function returns the next upcoming meeting after the current time.
func getNextMeeting(meetings []Meeting, currentTime time.Time) *Meeting {
	for _, meeting := range meetings {
		if meeting.DateTime.After(currentTime) {
			return &meeting
		}
	}
	return nil // No upcoming meetings found
}

func main() {
	currentTime := time.Now()

	meeting1 := Meeting{DateTime: time.Date(2022, 1, 1, 23, 59, 59, 0, time.UTC)}
	meeting2 := Meeting{DateTime: time.Date(2023, 5, 15, 10, 0, 0 ,0 ,time.UTC)}
	meeting3 := Meeting{DateTime: time.Date(2024 ,8 ,20 ,14 ,30 ,0 ,0,time.UTC)}

	meetings := []Meeting{meeting1, meeting2}

	nextMeeting := getNextMeeting(meetings,currentTime)

	if nextMeeting != nil {
	    fmt.Printf("The next scheduled meeting is on %s\n", nextMeeting.DateTime)
    } else{
        fmt.Println("There are no upcoming meetings.")
    }
}
