    header("Content-Type: application/gzip");
    ob_clean();
    flush();

    // Compress the file
    $handle = fopen($file, "r+"); // Open the file in read-write mode
    while (($data = fgets($handle)) !== false) {
        fwrite($handle, "--compressed-data\r\n" . $data);
    }

    // Close the file handle
    fclose($handle);

    // Get the compressed filename
    $compressedFilename = basename($file) . '.gz';

    // Create the dest directory if it doesn't exist
    if (!is_dir($destDir)) {
        mkdir($destDir, 0755, true); // Make directories recursively
    }

    // Move the compressed file to the dest directory
    move_uploaded_file(fopen($destDir . '/' . $compressedFilename, 'w'), $destDir . '/' . $compressedFilename);

    // Remove the temporary compressed file
    unlink($destDir . '/' . $compressedFilename);

    // Return the compressed filename
    return $compressedFilename;
}

