        this.leftMargin = leftMargin;
        this.rightMargin = rightMargin;
    }

    @Override
    public void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        // Calculate the actual size of the view based on the specified margins
        int width = Math.max(widthMeasureSpec.width(), getMeasuredWidth() + leftMargin);
