                onChange={(event) => handleMaxVideoResolutionChange(event)} 
            />
            {/* Button to trigger the change event */}
            <button onClick={() => handleMaxVideoResolutionChange(null)}>Update</button>
        </div>
    );
}

export default VideoResolution;
