// Function to calculate the Euclidean distance between two points
function calculateDistance(pointA: Point, pointB: Point): number {
    // Calculate the squared differences between corresponding coordinates
    const diffX = Math.pow(pointB.x - pointA.x, 2);
    const diffY = Math.pow(pointB.y - pointA.y, 2);

    // Use the Pythagorean theorem to calculate the square root of the sum of squares
    const distance = Math.sqrt(diffX + diffY);

    return distance;
}

