// Function to calculate the nth Fibonacci number
int fibonacci(int n) {
    return (n <= 1 ? n : fibonacci(n - 1) + fibonacci(n - 2));
}

// Function to calculate the sum of even terms in the Fibonacci sequence
int sumEvenTerms() {
    int sum = 0;
    for (int i = 0; i < 100; i++) {
        if (fibonacci(i) % 2 == 0) {
            sum += fibonacci(i);
        }
    }
    return sum;
}

int main() {
    int n;
    std::cout << "Enter the limit for the Fibonacci sequence: ";
    std::cin >> n;
    std::cout << "Fibonacci Sequence: ";
    for (int i = 0; i < n; i++) {
        std::cout << fibonacci(i) << ' ';
    }
    std::cout << '\n';
    std::cout << "Sum of even terms: " << sumEvenTerms() << '\n';

    return 0;
