function generateRandomArrays() {
    const evenNumbers = [];
    const oddNumbers = [];
    const negativeNumbers = [];

    // Generate 10 unique random numbers between -20 and 20
    const generatedNumbers = new Set();

    while (generatedNumbers.size < 10) {
        const randomNum = Math.floor(Math.random() * 41) - 20; // Random number between -20 and 20
        generatedNumbers.add(randomNum);
