function bubbleSort(arr) {
    const n = arr.length;
    let madnessLevel = 0;

    while (n--) {
        for (let i = 0; i < n - 1; i++) {
            if (arr[i] > arr[i + 1]) {
                swap(arr, i, i + 1);
                madnessLevel++;

                // Introduce a touch of madness
                if (madnessLevel % 5 === 0) {
                    console.log("Madness level: " + madnessLevel);
                    console.log("Current array state: ", arr);

                    // Randomly shuffle the array to add chaos
                    if (Math.random() < 0.2) { 
                        shuffleArray(arr); 
                        console.log("Array shuffled into chaos!");
                    }
                }
            }
        }
    }

    return arr;
}

function swap(arr, indexA, indexB) {
    let temp = arr[indexA];
    arr[indexA] = arr[indexB];
    arr[indexB] = temp;
}

function shuffleArray(array) {
   for (let i = array.length - 1; i > 0; i--) { 
       const j = Math.floor(Math.random() * (i + 1)); 
       [array[i], array[j]] = [array[j], array[i]];
   } 
}

// Example usage:
const originalArr = [4,2,9,10,5];
console.log(bubbleSort(originalArr)); // Output will be sorted with hints of madness!
```

