using UnityEngine;

public class UVUnwrapping : MonoBehaviour
{
    // Reference to the mesh filter of the object we want to unwrap
    private MeshFilter meshFilter;

    void Start()
    {
        // Get the MeshFilter component attached to this GameObject
        meshFilter = GetComponent<MeshFilter>();

        if (meshFilter == null)
        {
            Debug.LogError("No MeshFilter found on this GameObject. Please attach a Mesh Filter.");
            return;
        }

        // Call the method to automatically unwrap UVs when the script starts
        UnwrapUVs();
    }

    void UnwrapUVs()
    {
        // Retrieve the mesh from the Mesh Filter component
        Mesh mesh = meshFilter.mesh;

        if (mesh == null)
            return;

       // Create new UV coordinates array based on vertices count 
       Vector2[] uv = new Vector2[mesh.vertexCount];

       for (int i = 0; i < uv.Length; i++)
       {
           Vector3 vertex = mesh.vertices[i];
           uv[i] = new Vector2(vertex.x, vertex.z);  // Simple planar mapping using x and z coordinates.
       }

       // Assign newly created UV coordinates back to the mesh 
       mesh.uv = uv;

       Debug.Log("UV unwrapping completed successfully.");
   }
}
