```csharp
using System;
using System.Numerics;

public class FFTPatternMatcher
{
    public static void FFT(Complex[] input, Complex[] output, bool inverse = false)
    {
        int n = input.Length;
        if (n <= 1) return;

        // Split the array into even and odd parts
        Complex[] even = new Complex[n / 2];
        Complex[] odd = new Complex[n / 2];

        for (int i = 0; i < n / 2; i++)
        {
            even[i] = input[i * 2];
            odd[i] = input[i * 2 + 1];
        }

        // Recursive calls for FFT on both halves
        FFT(even, output, inverse);
        FFT(odd, output, inverse);

       // Combine results from the two halves
       double angleIncrement = (inverse ? -1 : 1) * (2.0 * Math.PI / n);
       for (int k = 0; k < n / 2; k++)
       {
           double angle = angleIncrement * k;
           var tExpKthRootOfUnity= new Complex(Math.Cos(angle), Math.Sin(angle)) * odd[k];

           output[k]     += tExpKthRootOfUnity;
           output[k + n/2] += even[k] - tExpKthRootOfUnity;
       }

       if(inverse)
       {
          for(int i=0;i<n;i++)
          {
              output[i]/=n; // Normalize the result in case of Inverse FFT.
          }
      }
    }

    public static void Main(string[] args)
    {
         int N=8; 
         var data=new Complex[N]{new Complex(0),new Complex(1),new Complex(3),
                                  new Complex(-4),new complex(-5),
                                  new complex(-6),new complex(-7),
                                  new complex(-8)};

         var result=new complex[N]; 

         Console.WriteLine("Input Data: ");
         foreach(var d in data){
             Console.WriteLine(d);
         }

         Console.WriteLine("\nFFT Result: ");

         fft(data,result); 

          foreach(var r in result){
             Console.WriteLine(r);
          }  


