// Define a protocol for the services that will be registered with the service locator
protocol Service {
    func execute()
}

// Implement concrete services conforming to the Service protocol
class ServiceA: Service {
    func execute() {
        print("Service A executed")
    }
}

class ServiceB: Service {
    func execute() {
        print("Service B executed")
    }
}

// Create a service locator class to manage and provide access to services
class ServiceLocator {

    // Dictionary to hold registered services by their type (using Any.Type as key)
    private var services = [String: Any]()

    // Method to register a service with its type as key
    func register<T>(service: T) {
        let key = String(describing: T.self)
        services[key] = service
    }

    // Method to retrieve a registered service by its type, returning nil if not found
    func getService<T>() -> T? {
        let key = String(describing: T.self)
        return services[key] as? T 
   }
}

// Usage example:
let locator = ServiceLocator()

// Registering different types of services with the locator.
locator.register(service: ServiceA())
locator.register(service: ServiceB())

// Retrieving and using the registered Services.
if let serviceA = locator.getService() as IService? { 
   serviceA.execute()  // Output will be "Service A executed"
}

if let serviceB = locator.getService(ServiceB.self) { 
   serviceB.execute()  // Output will be "Service B executed"
}
