      <input type="text" id="input-field" placeholder="Type your message..." />
      <button onclick="sendMessage()">Send</button>
    </div>

    <script>
      const chatContainer = document.getElementById('chat-container');

      function createMessageEl(message, className) {
        const messageEl = document.createElement('div');
        messageEl.className = `message ${className}`;
        messageEl.textContent = message;
        return messageEl;
      }

      function sendMessage() {
        const inputField = document.getElementById('input-field');
        const message = inputField.value.trim();

        if (message.length > 0) {
          // Display the user's message
          const userMessageEl = createMessageEl(message, 'user');
          chatContainer.appendChild(userMessageEl);

          // Generate the chatbot's response
          const botResponse = 'Hello! This is a simple chatbot.';
          const botMessageEl = createMessageEl(botResponse, 'chatbot');
          chatContainer.appendChild(botMessageEl);

          // Scroll to the bottom of the chat container
          chatContainer.scrollTop = chatContainer.scrollHeight;

          // Clear the input field
          inputField.value = '';
        }
      }

      // Allow users to press "Enter" to send a message
      document.getElementById('input-field').addEventListener('keydown', function (e) {
        if (e.keyCode === 13) {
          e.preventDefault(); // Prevent form submission
          sendMessage();
        }
      });
    </script>
  </body>
</html>
```

