% Analyze results
results = sim.get_results();
population_density_impact = results.entity_data('vehicle').position(:, end) - results.entity_data('vehicle').position(:, 1);
public_transit_impact = results.entity_data('public_transit').position(:, end) - results.entity_data('public_transit').position(:, 1);

% Visualize results
