```csharp
using System;
using System.Collections.Generic;

public abstract class WhiteBloodCell
{
    public string Name { get; set; }
    public abstract void Defend();

    public void ReleaseCytokine(Cytokine cytokine)
    {
        cytokine.Trigger();
    }
}

public class Neutrophil : WhiteBloodCell
{
    public Neutrophil()
    {
       Name = "Neutrophil";
    }

    public override void Defend()
    {
        Console.WriteLine($"The {Name} attacks and destroys bacteria directly.");
    }
}

public class Lymphocyte : WhiteBloodCell
{
    public Lymphocyte()
    {
        Name = "Lymphocyte";
    }

    public override void Defend()
    {
        Console.WriteLine($"The {Name} produces antibodies and coordinates the immune response.");
    }
}

public class Cytokine
{
    public string Name { get; set; }

    public Cytokine(string name)
    {
        Name = name;
    }

    public void Trigger()
    {
        Console.WriteLine($"{Name} gets released and helps in immune cell signaling and activation.");
    }
}

class Program
{
    static void Main(string[] args)
    {
        List<WhiteBloodCell> wbcList = new() 
        {
            new Neutrophil(),
            new Lymphocyte()
        };

        Cytokine il1 = new Cytokine("Interleukin-1");

        foreach (WhiteBloodCell wbc in wbcList)
        {
            wbc.Defend();
            wbc.ReleaseCytokine(il1);
        }
    }
}
```

