using System;

namespace TokenCounter
{
    class Program
    {
        static void Main(string[] args)
        {
            string inputString = "The quick brown fox jumps over the lazy dog.";
            int tokenCount = CountTokens(inputString);

            Console.WriteLine($"The input string has {tokenCount} tokens.");
        }

        // Function to count the number of tokens in a given input string
        public static int CountTokens(string input)
        {
            // Remove any leading or trailing white spaces
            input = input.Trim();

            // Check for empty or null input
            if (string.IsNullOrEmpty(input))
            {
                return 0;
            }

            // Split the input string separated by spaces
            string[] tokens = input.Split(new char[] { ' ' }, StringSplitOptions.RemoveEmptyEntries);

            // Return the count of tokens
            return tokens.Length;
        }
    }
}
